insert into review_t values ('2' ,'2', '7', '다으닝', '오늘 무료체험받고 20회 결제하고 왔습니다. 집과 가까운 곳들이 있긴하지만 커짐 믿고 찾아가서 무료체험과 상담을 받았는데요. pt몇번 받아본 경험이 있는데 첫날부터 이렇데 빡세게(?) 시켜주시는데는 첨이었네요.. ot개념처럼 생각하고 갔다가 입고간 운동복만 땀에 홀랑... 돌아오는길이 쌀쌀한 날씨덕에 더 춥더라구요ㅎㅎ... 사실 따지면 무료체험인데 첨부터 운동 잘 잡아주시고 인바디 상담도 꼼꼼하게 해주셔서(물론 어느정도 몸상태는 알고 있았지만 ㅠㅠ) 만족스러운 체험이었습니다. 이제는 본격 운동 들어가야되는데 그리고 나서는 무료체험말고 일반 후기로 작성할께요 ㅎㅎ 벌써 운동할 생각에 걱정되네요.ㅋㅋㅋㅋㅋㅋ', '5',null, sysdate , null);												
insert into review_t values ('3' ,'3', '8', '채영', '<1회 체험> 친구추천으로 커짐을 알게 되었고 다이어트를 목적으로 체험하게 되었는데 설명도 전문성 있게 잘 해주시고 운동 자세도 바르게 잘 잡아주셨습니다. 앞으로 관리도 잘 해주실것 같아서 바로 등록했습니다. 정규 수업도 엄청 기대되네요:) 추천합니다.', '5',null, sysdate , null);												
insert into review_t values ('4' ,'4', '9', 'jay', '지금까지 제가 배웠던 운동들은 운동 맛보기였던 것 같아요... 무료체험하면서 아직 갈길이 멀다는걸 체감하고, 제대로 배워보고 싶어서 등록했습니다. 전문성, 성의 다 갖춘 트레이너 쌤 찾고 싶었는데 잘 찾아온 것 같아요.', '5',null, sysdate , null);												
insert into review_t values ('5' ,'5', '10', 'TonyStark', '<1회 체험> 다른곳에서도 무료체험 한번 받아봤는데 너무 대충 설명해주셔서 실망했습니다. 근데 이번에는 선생님이 설명도 친절히 잘 해주시고 운동도 열심히 가르쳐주셔서 믿고 등록했습니다. 단백질 보충제도 주시고, 관리도 잘 해주시는 것 같습니다. 추천합니다!', '5',null, sysdate , null);												
insert into board_t values ('1', '6', '오늘의 등운동, SEATED ROW', '등운동은 크게 등 두께를 두껍게 만드는 운동과 등 넓이를 넓히는 운동으로 나눌 수 있습니다. 등 두께를 두껍게 하는 운동 종목으로는 로우류의 운동이 있습니다. 대표적으로 헬스장에서 많이 하시는 시티드 로우를 예로 들 수 있습니다. 시티드 로우 같은 경우에 보통 수축을 중점으로, 그 중량을 땡기는 것에만 중점을 두고 많이 운동을 하시는데 등운동은 수축보다는 이완에 중점을 두고 운동을 하셔야 합니다. 수축보다 이완에 집중을 하기 위해서는 중량 선택이 가장 중요합니다. 중량 선택에 관해서는 다음에 다시 다루도록 하겠습니다.',
insert into board_t values ('2', '7', '오늘의 가슴운동, CABLE CROSS OVER', '케이블 크로스 오버는 팔을 가운데로 모은다는 느낌보다는 앞쪽으로 손을 쭉 뻗는 프레스 느낌으로 해야한다. 가슴 중앙부랑 정면에 연장된 선이 있다고 생각하고 손을 그 선의 제일 먼 곳으로 보낸다는 느낌으로 프레스 하듯이 하면 혈액을 모을 수 있음.
insert into board_t values ('3', '8', '오늘의 하체운동, BARBELL SQUET', '웨이트 트레이닝의 꽃, 스쿼트. 옛날에는 스쿼트를 무릎이 발 앞으로 나가면 안된다고 많이 가르쳤다. 하지만 요새는 발의 넓이는 자신이 편한만큼 벌려서 스텐스를 잡으며 그 사이 공간으로 몸이 가라앉는 다는 느낌으로 편하게 앉아주면 된다.
